package corejava;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//To print even numbers
		for(int i=1;i<=10;i++) {
			if(i%2==0) {
				System.out.println(i);
			}
		}
		//To print odd numbers
		for(int i=1;i<=10;i++) {
			if(i%2!=0) {
				System.out.println(i);
			}
		}
	}

}
